pi = 3.
menu = 1



while menu == 1:
    try:
        valor_usuário_casasdecimais = int(input("\nquantas casas decimais do número de pi você deseja?\n"))
        
        if (valor_usuário_casasdecimais == 0):
            print("\nAté mais")
            break;
        elif (valor_usuário_casasdecimais > 0 ):
            print("\nContinuando...\n")
            
        
        print(f"O valor de pi com {valor_usuário_casasdecimais} casas decimais é: {pi: .{valor_usuário_casasdecimais}}")
        
        menu = input("\nDigite 1 para continuar no menu, 0 para sair:\n")
        
    except ValueError:
        print("Entrada inválida. Por favor, digite um número inteiro.")